// =================================================
// 設定エリア: Google Sheetsのノード名をここに入力
// =================================================
const sheetNodeName = "Google Sheets1"; 


// -------------------------------------------------
// 処理開始
// -------------------------------------------------

// 1. ファイル名の取得
// Execute for Each Itemモードの場合、前のノードのデータも現在のアイテムに対応するものが取れます
let fileName = "generated_image"; 

try {
  // $()を使って前のノードのデータを参照
  // 念のため .item があるか、.json があるかを確認
  const sheetNode = $(sheetNodeName);
  if (sheetNode && sheetNode.item && sheetNode.item.json && sheetNode.item.json["Filename"]) {
    fileName = sheetNode.item.json["Filename"];
  } else if (sheetNode && sheetNode.json && sheetNode.json["Filename"]) {
     // バージョンによってはこちらの場合もあります
     fileName = sheetNode.json["Filename"];
  }
} catch (error) {
  console.log("ファイル名取得エラー: デフォルト名を使用します");
}

// 2. HTTP Requestの結果から画像データを取得
// 【ここを修正しました】 items[0] ではなく $input.item を使います
const responseData = $input.item.json;
let base64String = null;

// 画像データを探す (candidates -> content -> parts -> inlineData)
if (responseData.candidates && responseData.candidates.length > 0) {
  const content = responseData.candidates[0].content;

  if (content && content.parts) {
    for (const part of content.parts) {
      if (part.inlineData && part.inlineData.data) {
        base64String = part.inlineData.data;
        break; 
      }
    }
  }
}

if (!base64String) {
  throw new Error("画像データが見つかりません。");
}

// 3. バイナリ変換
const binaryData = Buffer.from(base64String, 'base64');

// 4. 出力 (アイテムごとに返すので配列[]で囲む必要はありません)
return {
  json: {
    filename: fileName,
    message: "画像変換成功"
  },
  binary: {
    data: {
      data: binaryData.toString('base64'),
      mimeType: 'image/png',
      fileName: `${fileName}.png`
    }
  }
};

// Gemini TTS のレスポンス JSON
const res = $input.item.json;

// Base64 の PCM を取り出す
const base64String =
  res?.candidates?.[0]?.content?.parts?.[0]?.inlineData?.data;

if (!base64String) {
  throw new Error(
    'Gemini TTS の Base64 音声データが見つかりません。\nレスポンス: ' +
    JSON.stringify(res, null, 2)
  );
}

// Base64 → PCMバイト列
const pcmData = Buffer.from(base64String, 'base64');

// ---- ここから WAV のヘッダーを付ける処理 ----
const numChannels = 1;
const sampleRate = 24000;
const bitsPerSample = 16;

const byteRate = sampleRate * numChannels * (bitsPerSample / 8);
const blockAlign = numChannels * (bitsPerSample / 8);
const subchunk2Size = pcmData.length;
const chunkSize = 36 + subchunk2Size;

const header = Buffer.alloc(44);

// RIFFヘッダー
header.write('RIFF', 0);                    // ChunkID
header.writeUInt32LE(chunkSize, 4);         // ChunkSize
header.write('WAVE', 8);                    // Format

// fmt チャンク
header.write('fmt ', 12);                   // Subchunk1ID
header.writeUInt32LE(16, 16);               // Subchunk1Size (PCM)
header.writeUInt16LE(1, 20);                // AudioFormat (1 = PCM)
header.writeUInt16LE(numChannels, 22);      // NumChannels
header.writeUInt32LE(sampleRate, 24);       // SampleRate
header.writeUInt32LE(byteRate, 28);         // ByteRate
header.writeUInt16LE(blockAlign, 32);       // BlockAlign
header.writeUInt16LE(bitsPerSample, 34);    // BitsPerSample

// data チャンク
header.write('data', 36);                   // Subchunk2ID
header.writeUInt32LE(subchunk2Size, 40);    // Subchunk2Size

// ヘッダー + PCM本体 で WAV データ完成
const wavBuffer = Buffer.concat([header, pcmData]);

// n8n に返す
return {
  binary: {
    data: {
      data: wavBuffer,
      mimeType: 'audio/wav',
      fileName: 'voice.wav'
    }
  },
  json: res
};
